export default function Categories({activeCategories, handleCategorySelect, categories}){
    return (
        <>
            <h3> Select A category </h3>
            <div>Active Category: {activeCategories}</div>
            {/* {categories.length > 0 && */}
            {categories.map((Category) => {
                return (
                    <button
                        className="d-my"
                        // style={divStyle}
                        key={Category}
                        onClick={handleCategorySelect}
                        value={Category}>
                        {Category}
                    </button>
                )
            })}
     </>
    )
}
