import React, { useEffect, useState } from "react";
import getQuestionCategories from "./api";
import MyComponent from "./MyComponent";

const TriviaCategories = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [category, setCategory] = useState([]);

  useEffect(() => {
    let isMounted = true;

    const fetchCategories = async () => {
      try {
        const res = await getQuestionCategories();
        console.log(res);

        if (isMounted) {
          if (res && typeof res === "object") {
            const resCategories = Object.keys(res);
            setCategory(resCategories);
          } else {
            setError("Invalid API response for categories");
          }
          setLoading(false);
        }
      } catch (error) {
        if (isMounted) {
          setError("Error fetching categories");
          setLoading(false);
        }
      }
    };

    fetchCategories();

    // Clean up function to avoid memory leaks
    return () => {
      isMounted = false;
    };
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <MyComponent category={category} />
    </div>
  );
};

export default TriviaCategories;
