import React, { useState } from 'react';

const MyComponent = (props) => {
  const [display, setDisplay] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('');

  function showCategory(category) {
    setSelectedCategory(category);
    setDisplay(true);
  }

  return (
    <>
      <div
        style={{
          backgroundImage: `url("https://t4.ftcdn.net/jpg/03/45/88/07/360_F_345880772_zIT2mkdCzTthplO7xqaGGrMspN0jw0ll.jpg")`,
          backgroundSize: 'cover',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          width: '100%',
          height: '100vh',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        {/* Show the category and subcategories */}
        {display && selectedCategory && (
          <div
            style={{
              backgroundColor: 'rgba(255, 255, 255, 0.8)',
              padding: '20px',
              borderRadius: '10px',
              boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
              textAlign: 'center',
            }}
          >
            <h3>{selectedCategory}</h3>
            <ul>
              {props.categories[selectedCategory].map((subcategory, index) => (
                <li key={index}>{subcategory}</li>
              ))}
            </ul>
          </div>
        )}

        <div
          style={{
            backgroundColor: 'rgba(255, 255, 255, 0.8)',
            padding: '20px',
            borderRadius: '10px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
            textAlign: 'center',
          }}
          className={display ? 'd-none' : ''}
        >
          <h2>quiz</h2>
          {Object.keys(props.categories).map((category, index) => (
            <div key={index}>
              <button
                style={{
                  padding: '10px 20px',
                  fontSize: '16px',
                  borderRadius: '5px',
                  cursor: 'pointer',
                }}
                onClick={() => showCategory(category)}
              >
                {category}
              </button>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default MyComponent;
