import React, { useState } from 'react';

const MyComponent = (props) => {
  const [display, setDisplay] = useState(false);

  function showCategory(e, item) {
    e.preventDefault();
    setDisplay(true);
    console.log('Selected category:', item); // You can use 'item' here to perform further actions with the selected category.
  }

  return (
    <div className='page'>
      <div className={display ? '' : 'd-none'}>
        {props.category.map((item, index) => (
          <div
            key={index}
            style={{
              backgroundColor: 'rgba(255, 255, 255, 0.8)',
              padding: '20px',
              borderRadius: '10px',
              boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
              textAlign: 'center',
            }}
          >
            <button
              style={{
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                padding: '20px',
                borderRadius: '10px',
                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                textAlign: 'center',
                display: 'flex',
                flexWrap: 'wrap',
              }}
              onClick={(e) => showCategory(e, item)} // Pass 'item' to the event handler
              value={item}
            >
              {item}
            </button>
          </div>
        ))}
      </div>
      <div
        style={{
          backgroundColor: 'rgba(255, 255, 255, 0.8)',
          padding: '20px',
          borderRadius: '10px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          textAlign: 'center',
        }}
        className={display ? 'd-none' : ''}
      >
        <h2>quiz</h2>
        <button
          style={{
            padding: '10px 20px',
            fontSize: '16px',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
          onClick={(e) => showCategory(e, null)} // Pass null or omit the argument if not needed
        >
          Start
        </button>
      </div>
    </div>
  );
};

export default MyComponent;
