import React, { useState } from 'react';

const MyComponent = (props) => {
  const [display, setDisplay] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('');

  function showCategory(category) {
    setSelectedCategory(category);
    setDisplay(true);
  }

  function showQuiz() {
    setSelectedCategory('');
    setDisplay(false);
  }

  return (
    <div
      style={{
        backgroundImage: `url("https://t4.ftcdn.net/jpg/03/45/88/07/360_F_345880772_zIT2mkdCzTthplO7xqaGGrMspN0jw0ll.jpg")`,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
        width: '100%',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <div className={display ? 'd-none' : ''}>
        {Object.keys(props.category).map((item) => (
          <div
            key={item}
            style={{
              backgroundColor: 'rgba(255, 255, 255, 0.8)',
              padding: '20px',
              borderRadius: '10px',
              boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
              textAlign: 'center',
            }}
          >
            <button
              style={{
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                padding: '20px',
                borderRadius: '10px',
                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                textAlign: 'center',
                display: 'flex',
                flexWrap: 'wrap',
              }}
              onClick={() => showCategory(item)}
              value={item}
            >
              {item}
            </button>
          </div>
        ))}
      </div>
      <div
        style={{
          backgroundColor: 'rgba(255, 255, 255, 0.8)',
          padding: '20px',
          borderRadius: '10px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          textAlign: 'center',
        }}
        className={display ? '' : 'd-none'}
      >
        {display && selectedCategory ? (
          <h2>{selectedCategory} Quiz</h2>
        ) : (
          <h2>Quiz</h2>
        )}
        <button
          style={{
            padding: '10px 20px',
            fontSize: '16px',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
          onClick={showCategory}
        >

        </button>
      </div>
    </div>
  );
};

export default MyComponent;
