// Import the 'fetch' function from 'node-fetch' library if running in a Node.js environment
// For browsers, 'fetch' is natively available
// const fetch = require('node-fetch');

function getQuestionCategories() {
  return fetch(`https://the-trivia-api.com/v2/categories`)
      .then((res) => {
          // Check if the response status is okay (2xx) before parsing the JSON
          if (!res.ok) {
              throw new Error(`Error ${res.status}: Unable to fetch categories`);
          }
          return res.json();
      })
      .catch((err) => {
          // Handle errors during fetch or JSON parsing
          console.error(err);
          throw new Error('An error occurred while fetching question categories');
      });
}

function getQuestion() {
  return fetch(`https://the-trivia-api.com/v2/questions/search`)
      .then((res) => {
          // Check if the response status is okay (2xx) before parsing the JSON
          if (!res.ok) {
              throw new Error(`Error ${res.status}: Unable to fetch questions`);
          }
          return res.json();
      })
      .catch((err) => {
          // Handle errors during fetch or JSON parsing
          console.error(err);
          throw new Error('An error occurred while fetching questions');
      });
}

module.exports = { getQuestionCategories, getQuestion };
