import React, { useState } from 'react';

const MyComponent = (props) => {
  const [displayedCategory, setDisplayedCategory] = useState(null);

  function showCategory(e, category) {
    e.preventDefault();
    setDisplayedCategory(category);
  }

  function hideCategory() {
    setDisplayedCategory(null);
  }

  return (
    <>
      <div
        style={{
          backgroundImage: `url("https://t4.ftcdn.net/jpg/03/45/88/07/360_F_345880772_zIT2mkdCzTthplO7xqaGGrMspN0jw0ll.jpg")`,
          backgroundSize: 'cover',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          width: '100%',
          height: '100vh',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        {Object.keys(props).map((category) => {
          return (
            <div key={category}>
              {displayedCategory === category ? (
                <div
                  style={{
                    backgroundColor: 'rgba(255, 255, 255, 0.8)',
                    padding: '20px',
                    borderRadius: '10px',
                    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                    textAlign: 'center',
                  }}
                >
                  <button
                    style={{
                      backgroundColor: 'rgba(255, 255, 255, 0.8)',
                      padding: '20px',
                      borderRadius: '10px',
                      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                      textAlign: 'center',
                      display: 'flex',
                      flexWrap: 'wrap',
                    }}
                    onClick={hideCategory}
                  >
                    <h3>{category}</h3>
                    <ul>
                      {props[category].map((subcategory, index) => (
                        <li key={index}>{subcategory}</li>
                      ))}
                    </ul>
                  </button>
                </div>
              ) : (
                <div
                  style={{
                    backgroundColor: 'rgba(255, 255, 255, 0.8)',
                    padding: '20px',
                    borderRadius: '10px',
                    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                    textAlign: 'center',
                  }}
                >
                  <button
                    style={{
                      padding: '20px',
                      fontSize: '16px',
                      borderRadius: '10px',
                      cursor: 'pointer',
                    }}
                    onClick={(e) => showCategory(e, category)}
                  >
                    <h3>{category}</h3>
                    <span>Click to see subcategories</span>
                  </button>
                </div>
              )}
            </div>
          );
        })}
      </div>
    </>
  );
};

export default MyComponent;
